const burger = document.getElementById('sidebarToggle');//–ë—É—Ä–≥–µ—Ä
const sidebar = document.getElementById('sidebar'); //–õ–µ–≤—ã–π —Å–∞–π–¥–±–∞—Ä
const page = document.getElementById('page')//–≤—Å—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞
 const body = document.body;


burger.addEventListener('click', event =>{
   if(body.classList.contains('show-sidebar')){
        closeSidebar()
   } else{
        showSidebar()
   }
    
})

function showSidebar(){
    let mask  = document.createElement('div')
    mask.classList.add('page__mask');
    mask.addEventListener('click', closeSidebar)
    page.appendChild(mask);
    body.classList.add('show-sidebar')
}

function closeSidebar(){
    body.classList.remove('show-sidebar')
    document.querySelector('.page__mask').remove();
    
}



    const modalBtn = document.querySelectorAll("[data-modal]");
    //const body = document.body;
    const modalClose = document.querySelectorAll(".modal__close");
    const modal = document.querySelectorAll(".modal");

    modalBtn.forEach((item) => {
      item.addEventListener("click", (event) => {
        let $this = event.currentTarget;
        console.log("üöÄ ~ item.addEventListener ~ this:", this)
        
        let modalId = $this.getAttribute("data-modal");
        let modal = document.getElementById(modalId);
        let modalContent = modal.querySelector(".modal__content");

        modalContent.addEventListener("click", (event) => {
          event.stopPropagation();
        });

        modal.classList.add("show");
        body.classList.add("no-scroll");

        setTimeout(() => {
          modalContent.style.transform = "none";
          modalContent.style.opacity = "1";
        }, 1);
      });
    });

    modalClose.forEach((item) => {
      item.addEventListener("click", (event) => {
        let currentModal = event.currentTarget.closest(".modal"); //event.currentTarget –æ–±–æ–∑–Ω–∞—á–∞–µ—Ç —ç–ª–µ–º–µ–Ω—Ç, –∫ –∫–æ—Ç–æ—Ä–æ–º—É –ø—Ä–∏–≤—è–∑–∞–Ω –æ–±—Ä–∞–±–æ—Ç—á–∏–∫ —ç—Ç–æ–≥–æ —Å–æ–±—ã—Ç–∏—è.

        closeModal(currentModal);
      });
    });

    modal.forEach((item) => {
      item.addEventListener("click", (event) => {
        let currentModal = event.currentTarget;
        console.log("üöÄ ~ item.addEventListener ~ currentModal:", currentModal);

        closeModal(currentModal);
      });
    });

    function closeModal(currentModal) {
      let modalContent = currentModal.querySelector(".modal__content");
      modalContent.removeAttribute("style");
      setTimeout(() => {
        currentModal.classList.remove("show");
        body.classList.remove("no-scroll");
      }, 200);
    }
const textArea = document.querySelectorAll('[data-autoresize]');

textArea.forEach(item =>{
    let textareaH = item.offsetHeight;
    
    item.addEventListener('input', event =>{
        
        let $this = event.target;

        $this.style.height = textareaH + "px";
        $this.style.height = $this.scrollHeight + 'px';
        
    });
});
document.addEventListener("DOMContentLoaded", function () {
  const header = document.querySelector('.header__block')
  const packingSwitch = document.querySelector('.header__switches')
  window.addEventListener("scroll", function () {
    const winTop = window.scrollY;

    if (winTop >= 30) {
      packingSwitch.classList.add('switch');
      setTimeout(()=>{
        document.body.classList.add("sticky-header");
      },100)
      
    } else {
      packingSwitch.classList.remove('switch');
      setTimeout(() => {
        document.body.classList.remove("sticky-header");
      }, 100)
      
    }
  });
});
const footer = document.querySelector(".footer-content");
console.log("üöÄ ~ footer:", footer)
const sentinel = document.querySelector(".sentinel");
const footerWrapList = document.querySelector(".footer-wrapper__list");

const observer = new IntersectionObserver((entries) => {
    entries.forEach(entry => {
        if (entry.isIntersecting) {
            footer.classList.add("expanded");
            footerWrapList.classList.remove("expanded");
        } else {
            footer.classList.remove("expanded");
            footerWrapList.classList.add("expanded");
        }
    });
}, {
    root: null, // —Å–ª–µ–¥–∏–º –∑–∞ viewport
    threshold: 0.5 // –∫–∞–∫ –º–∏–Ω–∏–º—É–º –Ω–∞–ø–æ–ª–æ–≤–∏–Ω—É –≤–∏–¥–Ω–æ sentinel
});

observer.observe(sentinel);
(function () {
    const container = document.getElementById('bubbles');
    const count = 128; // small numbers look nice too :)
    const frag = document.createDocumentFragment();

    for (let i = 0; i < count; i++) {
        const d = document.createElement('div');
        d.className = 'bubble';
        const size = 2 + Math.random() * 4;       // rem
        const distance = 2 + Math.random() * 2;   // rem
        const position = -5 + Math.random() * 110; // %
        const time = 1 + Math.random() * 1.5;       // s
        const delay = -(2 + Math.random() * 2);   // s
        d.style.setProperty('--size', size + 'rem');
        d.style.setProperty('--distance', distance + 'rem');
        d.style.setProperty('--position', position + '%');
        d.style.setProperty('--time', time + 's');
        d.style.setProperty('--delay', delay + 's');
        frag.appendChild(d);
    }

    container.appendChild(frag);
})();
const btnLightMode = document.querySelector('.tgl-btn');
const btnColorMode = document.querySelectorAll('.header__switches-color');

const themeProfile = document.querySelector(".profile");
const defaultAva = document.querySelector(".profile__avatar");
const avaImg = document.createElement("img");


if (!localStorage.getItem('colorMode')){
    avaImg.className = "profile__avatar";
    avaImg.src = `assets/images/ava-blue.jpg`;
    avaImg.alt = "ava";

    themeProfile.prepend(avaImg);
}


const theme = localStorage.getItem('darkMode');
document.body.classList.toggle('dark', theme === 'dark');
document.body.classList.toggle('light', theme === 'light');
// 3-1 color-theme
if (localStorage.getItem('colorMode') === 'pink') {
    document?.body.classList.remove("blue", "green")
    document?.body.classList.add("pink");

    avaImg.className = "profile__avatar";
    avaImg.src = `assets/images/ava-pink.jpg`;
    avaImg.alt = "ava";

    themeProfile.prepend(avaImg);
}
else if (localStorage.getItem('colorMode') === 'blue') {
    document?.body.classList.remove("pink", "green")
    document?.body.classList.add("blue")

    avaImg.className = "profile__avatar";
    avaImg.src = `assets/images/ava-blue.jpg`;
    avaImg.alt = "ava";

    themeProfile.prepend(avaImg);
}
else if (localStorage.getItem('colorMode') === 'green') {
    document?.body.classList.remove("pink", "blue")
    document?.body.classList.add("green")

    avaImg.className = "profile__avatar profile__avatar--green";
    avaImg.src = `assets/images/ava-green.jpg`;
    avaImg.alt = "ava";

    themeProfile.prepend(avaImg);
}



btnLightMode.onclick = function () {
    const isCurrentlyDark = document.body.classList.contains("dark");

    if (isCurrentlyDark) {
        document.body.classList.remove("dark");
        document.body.classList.add("light");
        localStorage.setItem("darkMode", "light");
    } else {
        document.body.classList.remove("light");
        document.body.classList.add("dark");
        localStorage.setItem("darkMode", "dark");
    }
}


btnColorMode.forEach((item) => {
    item.addEventListener('click', function () {
        if (item.classList.contains('header__switches--pink')) {
            setColorTheme("pink");

        }
        else if (item.classList.contains('header__switches--blue')) {
            setColorTheme("blue");
        }
        else if (item.classList.contains('header__switches--green')) {
            setColorTheme("green");
        }

    })
})

function setColorTheme(color) {
    document.body.classList.remove("pink", "blue", "green");
    document.body.classList.add(color);
    localStorage.setItem("colorMode", color);

    avaImg.className = "profile__avatar profile__avatar--green";
    avaImg.src = `assets/images/ava-${color}.jpg`;
    avaImg.alt = "ava";

    themeProfile.prepend(avaImg);
}
var animateButton = function (e) {

    e.preventDefault;
    //reset animation
    e.target.classList.remove('animate');

    e.target.classList.add('animate');
    setTimeout(function () {
        e.target.classList.remove('animate');
    }, 700);
};

var bubblyButtons = document.querySelectorAll(".color-card-effect");

for (var i = 0; i < bubblyButtons.length; i++) {
    bubblyButtons[i].addEventListener('click', animateButton, false);
}
const resizeBtn = document.querySelectorAll("[data-resize-btn]");
const sidebarBox = document.querySelector('.sidebar')

resizeBtn.forEach((item)=> {

    item.addEventListener("click", function (e) {
        e.preventDefault();
        document.body.classList.toggle("sb-expanded");
        if (!document.body.classList.contains("sb-expanded")){
            sidebarBox.style.boxShadow = "none";   
        }
        else{
            sidebarBox.style.boxShadow = "5px 0 10px var(--trident-bg)"; 
        }
    });
})
const marker1 = document.querySelector('.marker-1');
const marker2 = document.querySelector('.marker-2');
const navLinkLeft = document.querySelectorAll('.nav__link--left');
const navLinkRight = document.querySelectorAll('.nav__link--right');

function showMarker(markerToShow, markerToHide, e) {
    markerToShow.style.left = e.offsetLeft + 'px';
    markerToShow.style.width = e.offsetWidth + 'px';

    markerToShow.style.display = 'block';
    markerToShow.style.opacity = '1';

    markerToHide.style.opacity = '0';
    markerToHide.style.display = 'none';

    
}


navLinkLeft.forEach(link => {
    link.addEventListener('mouseenter', e => {
        showMarker(marker1, marker2, e.target);
    });
});


navLinkRight.forEach(link => {
    link.addEventListener('mouseenter', e => {
        showMarker(marker2, marker1, e.target);
    });
});
