document.addEventListener("click", function (event) {
  let id = event.target.dataset.toggleHeart;
  if (!id) return;

  toggleHeartMode();
});

function toggleHeartMode(forceState) {
  const footerHeart = document.querySelector(".footer-heart");
  const body = document.body;

  // –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ç–µ–∫—É—â–µ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ (–µ—Å–ª–∏ forceState –Ω–µ –ø–µ—Ä–µ–¥–∞–Ω)
  let isActive =
    forceState !== undefined
      ? forceState === "active"
      : !footerHeart.classList.contains("heart");

  footerHeart.classList.toggle("heart", isActive);

  if (isActive) {
    localStorage.setItem("heartMode", "active");

    let lastTime = 0;
    let delay = 50;

    body._heartMouseMoveHandler = function (event) {
      if (
        event.target.closest(
          "a, p, img, svg, button, input, textarea, .no-hearts, textarea, form, .stories"
        )
      ) {
        return;
      }

      const now = Date.now();
      if (now - lastTime < delay) return;
      lastTime = now;

      const span = document.createElement("span");
      span.className = "heart-follow";

      const x = event.clientX;
      const y = event.clientY;

      span.style.left = x + "px";
      span.style.top = y + "px";
      const size = Math.floor(Math.random() * 100);
      span.style.width = size + "px";
      span.style.height = size + "px";

      body.appendChild(span);
      setTimeout(() => span.remove(), 3000);
    };

    body.addEventListener("mousemove", body._heartMouseMoveHandler);
  } else {
    localStorage.removeItem("heartMode");

    if (body._heartMouseMoveHandler) {
      body.removeEventListener("mousemove", body._heartMouseMoveHandler);
      delete body._heartMouseMoveHandler;
    }
  }
}

// üîÅ –ü—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Å—Ç—Ä–∞–Ω–∏—Ü—ã –≤–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ
document.addEventListener("DOMContentLoaded", () => {
  const saved = localStorage.getItem("heartMode");
  if (saved === "active") {
    toggleHeartMode("active");
  }
});

